'use strict';

// MAIN //

/**
* Returns the maximum value.
*
* @param {number} x - first number
* @param {number} y - second number
* @returns {number} maximum value
*
* @example
* var v = max( 3.14, 4.2 );
* // returns 4.2
*
* @example
* var v = max( 3.14, NaN );
* // returns NaN
*
* @example
* var v = max( NaN, 3.14 );
* // returns 3.14
*
* @example
* var v = max( -0.0, +0.0 );
* // returns +0.0
*
* @example
* var v = max( +0.0, -0.0 );
* // returns -0.0
*/
function max( x, y ) {
	if ( x > y ) {
		return x;
	}
	return y;
} // end FUNCTION max()


// EXPORTS //

module.exports = max;
