'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var incrmvariance = require( './../lib' );

var accumulator;
var s2;
var v;
var i;

// Initialize an accumulator:
accumulator = incrmvariance( 5 );

// For each simulated datum, update the moving unbiased sample variance...
console.log( '\nValue\tSample Variance\n' );
for ( i = 0; i < 100; i++ ) {
	v = randu() * 100.0;
	s2 = accumulator( v );
	console.log( '%d\t%d', v.toFixed( 4 ), s2.toFixed( 4 ) );
}
