'use strict';

/**
* Calculates the dot product of two vectors
*
* @param {NumericArray} x - first vector
* @param {NumericArray} y - second vector
* @returns {number} dot product
*
* @example
* var x = [ 1, 2, 3 ];
* var y = [ 1, 2, 2 ];
*
* var ret = dot( x, y );
* // returns 11
*/
function dot( x, y ) {
	var len = x.length;
	var ret = 0;
	var i;

	for ( i = 0; i < len; i++ ) {
		ret += x[ i ] * y[ i ];
	}
	return ret;
} // end FUNCTION dot()


// EXPORTS //

module.exports = dot;
