'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetical order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// MAIN //

/**
* Top-level namespace.
*
* @namespace random
*/
var random = {};

/**
* @name sample
* @memberof random
* @readonly
* @type {Function}
* @see {@link module:@stdlib/math/random/sample}
*/
setReadOnly( random, 'sample', require( '@stdlib/math/random/sample' ) );

/**
* @name shuffle
* @memberof random
* @readonly
* @type {Function}
* @see {@link module:@stdlib/math/random/shuffle}
*/
setReadOnly( random, 'shuffle', require( '@stdlib/math/random/shuffle' ) );


// EXPORTS //

module.exports = random;
