const { performance } = require('perf_hooks');
const abs = require('./index'); // Import your function

// Create random data
const generateRandomData = (size) => {
  return Array.from({ length: size }, () => Math.floor(Math.random() * 1000) - 500);
};

// Benchmark function
const benchmark = (func, data) => {
  const start = performance.now();
  func(data);
  const end = performance.now();
  return end - start;
};

// Test different sizes of data
const sizes = [10, 100, 1000, 10000];
sizes.forEach(size => {
  const data = generateRandomData(size);
  const time = benchmark(abs, data);
  console.log(`Time for size ${size}: ${time} ms`);
});
