/**
* Compute the Fibonacci number.
*
* @module @stdlib/math/special/fibonacci
*
* @example
* var fibonacci = require( '@stdlib/math/special/fibonacci' );
*
* var y = fibonacci( 10 );
* // returns 55
*
* @example
* var Float64Array = require( '@stdlib/array/float64' );
* var fibonacci = require( '@stdlib/math/special/fibonacci' );
*
* var x = new Float64Array( [ 5, 10, 15 ] );
*
* var y = fibonacci( x );
* // returns <Float64Array>[ 5, 55, 610 ]
*
* @example
* var fibonacci = require( '@stdlib/math/special/fibonacci' );
*
* var x = [ 5, 10, 15 ];
*
* var y = fibonacci( x );
* // returns [ 5, 55, 610 ]
*
* @example
* var array = require( '@stdlib/ndarray/array' );
* var fibonacci = require( '@stdlib/math/special/fibonacci' );
*
* var x = array( [ [ 5, 10 ], [ 15, 20 ] ] );
* // returns <ndarray>
*
* var y = fibonacci( x );
* // returns <ndarray>
*
* var v = y.get( 0, 1 );
* // 55
*/

// MODULES //

var join = require( 'path' ).join;
var tryRequire = require( '@stdlib/utils/try-require' );
var javascript = require( './main.js' );

// MAIN //

var main;
var tmp = tryRequire( join( __dirname, './native.js' ) );
if ( tmp instanceof Error ) {
    main = javascript;
} else {
    main = tmp;
}

// EXPORTS //

module.exports = main;

// exports: { "assign": "main.assign" }
