#include <math.h>
#include <float.h>

/**
* Computes the index of a given Fibonacci number.
*
* @param F   Fibonacci number
* @return    Fibonacci index
*/
double stdlib_base_fibonacci_index( const double F ) {
    if ( F < 0 || isnan(F) || isinf(F) ) {
        return NAN;
    }
    if ( F == 0 ) return 0;
    if ( F == 1 ) return 1;

    const double PHI = 1.618033988749895;
    const double SQRT5 = 2.23606797749979;
    double n = log(F * SQRT5 + 0.5) / log(PHI);
    return round(n);
}
