'use strict';

/**
* Sums the elements of a numeric array.
*
* @private
* @param {NumericArray} arr - input array
* @returns {number} sum
*
* @example
* var arr = [ 2, 3, 5, 4 ];
* var out = sum( arr );
* // returns 14
*/
function sum( arr ) {
	var len = arr.length;
	var out = 0;
	var i;

	for ( i = 0; i < len; i++ ) {
		out += arr[ i ];
	}
	return out;
} // end FUNCTION sum()


// EXPORTS //

module.exports = sum;
