'use strict';

/**
* Compute a count incrementally.
*
* @module @stdlib/math/statistics/incrcount
*
* @example
* var incrcount = require( '@stdlib/math/statistics/incrcount' );
*
* var accumulator = incrcount();
*
* var count = accumulator();
* // returns 1
*
* count = accumulator( 2.0 );
* // returns 2
*
* count = accumulator( -5.0 );
* // returns 3
*
* count = accumulator();
* // returns 3
*/

// MODULES //

var incrcount = require( './incrcount.js' );


// EXPORTS //

module.exports = incrcount;
