'use strict';

/**
* Compute a maximum value incrementally.
*
* @module @stdlib/math/statistics/incrmax
*
* @example
* var incrmax = require( '@stdlib/math/statistics/incrmax' );
*
* var accumulator = incrmax();
*
* var max = accumulator();
* // returns null
*
* max = accumulator( 3.14 );
* // returns 3.14
*
* max = accumulator( -5.0 );
* // returns 3.14
*
* max = accumulator( 10.1 );
* // returns 10.1
*
* max = accumulator();
* // returns 10.1
*/

// MODULES //

var incrmax = require( './incrmax.js' );


// EXPORTS //

module.exports = incrmax;
