'use strict';

/**
* Compute a moving arithmetic mean incrementally.
*
* @module @stdlib/math/statistics/incrmmean
*
* @example
* var incrmmean = require( '@stdlib/math/statistics/incrmmean' );
*
* var accumulator = incrmmean( 3 );
*
* var mu = accumulator();
* // returns null
*
* mu = accumulator( 2.0 );
* // returns 2.0
*
* mu = accumulator( -5.0 );
* // returns -1.5
*
* mu = accumulator( 3.0 );
* // returns 0.0
*
* mu = accumulator( 5.0 );
* // returns 1.0
*
* mu = accumulator();
* // returns 1.0
*/

// MODULES //

var incrmmean = require( './incrmmean.js' );


// EXPORTS //

module.exports = incrmmean;
