'use strict';

/**
* Compute a sum incrementally.
*
* @module @stdlib/math/statistics/incrsum
*
* @example
* var incrsum = require( '@stdlib/math/statistics/incrsum' );
*
* var accumulator = incrsum();
*
* var sum = accumulator();
* // returns 0.0
*
* sum = accumulator( 2.0 );
* // returns 2.0
*
* sum = accumulator( -5.0 );
* // returns -3.0
*
* sum = accumulator();
* // returns -3.0
*/

// MODULES //

var incrsum = require( './incrsum.js' );


// EXPORTS //

module.exports = incrsum;
