'use strict';

var rnorm = require( '@stdlib/math/base/random/normal' );
var sqrt = require( '@stdlib/math/base/special/sqrt' );
var pcorrtest = require( './../lib' );

var table;
var out;
var rho;
var x;
var y;
var i;

rho = 0.5;
x = new Array( 300 );
y = new Array( 300 );
for ( i = 0; i < 300; i++ ) {
	x[ i ] = rnorm( 0.0, 1.0 );
	y[ i ] = ( rho * x[ i ] ) + rnorm( 0.0, sqrt( 1.0 - (rho*rho) ) );
}

out = pcorrtest( x, y );
table = out.print();
console.log( table );

out = pcorrtest( x, y, {
	'rho': 0.5
});
table = out.print();
console.log( table );
