'use strict';

/**
* One-way analysis of variance.
*
* @module @stdlib/math/stats/anova1
*
* @example
* var anova1 = require( '@stdlib/math/stats/anova1' );
* var x = [1, 3, 5, 2, 4, 6, 8, 7, 10, 11, 12, 15];
* var f = ['control', 'treatA', 'treatB', 'treatC', 'control', 'treatA', 'treatB', 'treatC', 'control', 'treatA', 'treatB', 'treatC' ];
*
* var out = anova1(x, f);
* /* returns
*     {
*         'treatmentDf': 11,
*         'treatmentSS': ~20.667,
*         'treatmentMSS': ~6.889,
*         'errorDf': 8,
*         'errorSS': ~185.333,
*         'errorMSS': ~23.1667,
*         'statistic': ~0.297,
*         'pValue': ~0.826,
*         'means': { 'control': { 'mean': 5.0, 'sampleSize: 3.0, 'SD': ~4.583 },
*            'treatA': { 'mean': ~6.667, 'sampleSize': 3.0, 'SD': ~4.041 },
*            'treatB': { 'mean': ~8.333, 'sampleSize': 3.0, 'SD': ~3.512 },
*            'treatC': { 'mean': 8.0, 'sampleSize': 3.0, 'SD': ~6.557 }
*         },
*         'method': 'One-Way ANOVA'
*     }
* *\/
*
* var table = out.print();
* /*
* One-Way ANOVA
*
* Null Hypothesis: All Means Equal
* Alternate Hypothesis: At Least one Mean not Equal
*
* 		df	Sum Squares	Mean Sum Sq.	F Score	P Value
* Treatment	11	15		5		0.3125	0.8161
* Errors		8	128		16
*
* *\/
*/

// MODULES //

var anova1 = require( './anova1.js' );


// EXPORTS //

module.exports = anova1;

