'use strict';

// MODULES //

var tape = require( 'tape' );
var randu = require( '@stdlib/math/base/random/randu' );
var abs = require( '@stdlib/math/base/special/abs' );
var EPS = require( '@stdlib/math/constants/float64-eps' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var incrkurtosis = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof incrkurtosis, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns an accumulator function', function test( t ) {
	t.equal( typeof incrkurtosis(), 'function', 'returns a function' );
	t.end();
});

tape( 'the accumulator function incrementally computes a corrected sample excess kurtosis', function test( t ) {
	var expected;
	var actual;
	var delta;
	var data;
	var tol;
	var acc;
	var i;

	data = [ 2.0, 2.0, -4.0, -4.0, 1.5, -10 ];

	// Check against the kurtosis function of the `e1071` R package:
	expected = [
		null,
		null,
		null,
		-6,
		-3.309339678762642,
		-0.1906596382525679
	];

	acc = incrkurtosis();

	for ( i = 0; i < data.length; i++ ) {
		actual = acc( data[ i ] );
		if ( expected[i] === null ) {
			t.equal( actual, null, 'returns null' );
		} else {
			delta = abs( actual - expected[ i ] );
			tol = 1.5 * EPS * abs( expected[ i ] );
			t.ok( delta <= tol, 'within tolerance. actual: '+actual+'. E: '+expected[i]+' Î”: '+delta+'. tol: '+tol );
		}
	}

	t.end();
});

tape( 'if not provided an input value, the accumulator function returns the current corrected sample excess kurtosis', function test( t ) {
	var data;
	var acc;
	var i;

	data = [ -10.0, -10.0, 10.0, 10.0 ];
	acc = incrkurtosis();
	for ( i = 0; i < data.length; i++ ) {
		acc( data[ i ] );
	}

	t.equal( acc(), -6.0, 'returns the current accumulated corrected sample excess kurtosis' );
	t.end();
});

tape( 'the corrected sample excess kurtosis is `null` until at least 4 datums have been provided', function test( t ) {
	var acc;
	var out;

	acc = incrkurtosis();

	out = acc();
	t.equal( out, null, 'returns null' );

	out = acc( 2.0 );
	t.equal( out, null, 'returns null' );

	out = acc( 8.0 );
	t.equal( out, null, 'returns null' );

	out = acc( -4.0 );
	t.equal( out, null, 'returns null' );

	out = acc( 3.0 );
	t.notEqual( out, null, 'does not return null' );

	t.end();
});

tape( 'if provided a `NaN` or a value which when computed evaluates to `NaN`, the accumulator returns `NaN` for all future invocations', function test( t ) {
	var kurtosis;
	var acc;
	var i;

	acc = incrkurtosis();

	acc( 2.0 );
	acc( 3.0 );
	acc( -4.0 );
	acc( -5.0 );
	acc( NaN );

	for ( i = 0; i < 1000; i++ ) {
		kurtosis = acc( randu() );
		t.equal( isnan( kurtosis ), true, 'returns NaN' );
	}
	t.end();
});
