var kde2d = require( '../lib');
var normal = require( '@stdlib/random/base/normal' );

var randX;
var randY;
var out;
var i;
var x;
var y;
var n;

n = 100

x = new Array(n);
y = new Array(n);

randX = normal.factory(3, 1.2);
randY = normal.factory(10, 4.5);

for (i = 0; i < n; i++) {
	x[i] = randX();
	y[i] = randY();
}

out = kde2d(x, y);
console.log(out);