'use strict';
// MAIN //
/**
* Returns the arithmetic range of an array.
*
* @param {NumericArray} arr - input array
* @returns {NumericArray} arithmetic range
*/
function range( arr ) {
    var len;
    var min;
    var max;
    var x;
    var i;
    len = arr.length;
    min = arr[ 0 ];
    max = min;
    for ( i = 1; i < len; i++ ) {
        x = arr[ i ];
        if ( x < min ) {
            min = x;
        } else if ( x > max ) {
            max = x;
        }
    }
    return [ min, max ];
}
// EXPORTS //
module.exports = range;