'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetical order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// MAIN //

/**
* Top-level namespace.
*
* @namespace ns
*/
var ns = {};

/**
* @name add
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/math/strided/ops/add}
*/
setReadOnly( ns, 'add', require( '@stdlib/math/strided/ops/add' ) );

/**
* @name addBy
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/math/strided/ops/add-by}
*/
setReadOnly( ns, 'addBy', require( '@stdlib/math/strided/ops/add-by' ) );

/**
* @name mul
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/math/strided/ops/mul}
*/
setReadOnly( ns, 'mul', require( '@stdlib/math/strided/ops/mul' ) );

/**
* @name sub
* @memberof ns
* @readonly
* @type {Function}
* @see {@link module:@stdlib/math/strided/ops/sub}
*/
setReadOnly( ns, 'sub', require( '@stdlib/math/strided/ops/sub' ) );


// EXPORTS //

module.exports = ns;
