'use strict';

// MODULES //

var INT8 = require( '@stdlib/math/constants/int8-max' );
var UINT8 = require( '@stdlib/math/constants/uint8-max' );
var INT16 = require( '@stdlib/math/constants/int16-max' );
var UINT16 = require( '@stdlib/math/constants/uint16-max' );
var INT32 = require( '@stdlib/math/constants/int32-max' );
var UINT32 = require( '@stdlib/math/constants/uint32-max' );
var FLOAT16 = require( '@stdlib/math/constants/float16-pinf' );
var FLOAT32 = require( '@stdlib/math/constants/float32-pinf' );
var FLOAT64 = require( '@stdlib/math/constants/float64-pinf' );


// MAIN //

/**
* Returns the maximum value of a specified numeric type.
*
* @param {string} dtype - numeric type
* @throws {TypeError} must provide a recognized numeric type
* @returns {number} maximum value
*
* @example
* var m = typemax( 'int8' );
* // returns 127
*
* @example
* var m = typemax( 'uint8' );
* // returns 255
*
* @example
* var m = typemax( 'int16' );
* // returns 32767
*/
function typemax( dtype ) {
	switch ( dtype ) {
	case 'float64':
		return FLOAT64;
	case 'int32':
		return INT32;
	case 'uint32':
		return UINT32;
	case 'float32':
		return FLOAT32;
	case 'int8':
		return INT8;
	case 'uint8':
		// Fall-through...
	case 'uint8c': // eslint-disable-line no-fallthrough
		return UINT8;
	case 'int16':
		return INT16;
	case 'uint16':
		return UINT16;
	case 'float16':
		return FLOAT16;
	default:
		throw new TypeError( 'invalid input argument. Must provide a recognized type. Value: `'+dtype+'`.' );
	}
} // end FUNCTION typemax()


// EXPORTS //

module.exports = typemax;
