'use strict';

// MODULES //

var merge = require( '@stdlib/utils/merge' );
var DIST = require( '@stdlib/math/base/dist' );


// ASSIGN //

/**
* Assign `dist` object to a namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* assign( ns );
* // ns => {...}
*/
function assign( ns ) {
	ns.dist = DIST;
	return ns;
} // end FUNCTION assign()


// EXPORTS //

module.exports = assign;
