#include <stdint.h>
#include "stdlib/ndarray/base/minmax_view_buffer_index.h"

/**
* Computes the minimum and maximum linear indices in an underlying data buffer accessible to an array view.
*
* @param out      2-element output array
* @param ndims    number of dimensions
* @param shape    array shape (dimensions)
* @param strides  array strides
* @param offset   index offset
* @return         maximum linear index
*
* @example
* #include "stdlib/ndarray/base/minmax_view_buffer_index.h"
*
* int64_t out[ 2 ];
* int64_t ndims = 2;
* int64_t shape[] = { 10, 10 };
* int64_t strides[] = { 10, 1 };
* int64_t offset = 0;
*
* stdlib_ndarray_minmax_view_buffer_index( out, ndims, shape, strides, offset );
*
* int64_t min = out[ 0 ];
* // returns 0
*
* int64_t max = out[ 1 ];
* // returns 99
*/
void stdlib_ndarray_minmax_view_buffer_index( int64_t *out, int64_t ndims, int64_t *shape, int64_t *strides, int64_t offset ) {
	int64_t min;
	int64_t max;
	int64_t i;

	min = offset;
	max = offset;
	for ( i = 0; i < ndims; i++ ) {
		if ( strides[ i ] > 0 ) {
			max += strides[ i ] * (shape[ i ] - 1);
		} else if ( strides[ i ] < 0 ) {
			min += strides[ i ] * (shape[ i ] - 1); // decrements min
		}
	}
	out[ 0 ] = min;
	out[ 1 ] = max;
}
