'use strict';

// MODULES //

var abs = require( '@stdlib/math/base/special/abs' );


// MAIN //

/**
* Determines the order of a multidimensional array based on a provided stride array.
*
* @param {IntegerArray} strides - stride array
* @returns {string} order
*
* @example
* var strides2order = require( '@stdlib/ndarray/base/strides2order' );
*
* var order = strides2order( [ 2, 1 ] );
* // returns 'row-major'
*
* order = strides2order( [ 1, 2 ] );
* // returns 'column-major'
*
* order = strides2order( [ 1, 1, 1 ] );
* // returns 'both'
*
* order = strides2order( [ 2, 3, 1 ] );
* // returns 'none'
*/
function strides2order( strides ) {
	var column;
	var ndims;
	var row;
	var s1;
	var s2;
	var i;

	ndims = strides.length;
	if ( ndims === 0 ) {
		return 'none';
	}
	column = true;
	row = true;

	s1 = abs( strides[ 0 ] );
	for ( i = 1; i < ndims; i++ ) {
		s2 = abs( strides[ i ] );
		if ( column && s2 < s1 ) {
			column = false;
		} else if ( row && s2 > s1 ) {
			row = false;
		}
		if ( row || column ) {
			s1 = s2;
		} else {
			return 'none';
		}
	}
	if ( row && column ) {
		return 'both';
	}
	if ( row ) {
		return 'row-major';
	}
	return 'column-major';
} // end FUNCTION strides2order()


// EXPORTS //

module.exports = strides2order;
