'use strict';

var discreteUniform = require( '@stdlib/random/base/discrete-uniform' );
var wrapIndex = require( './../lib' );

var idx;
var out;
var i;

for ( i = 0; i < 100; i++ ) {
	idx = discreteUniform( -20, 20 );
	out = wrapIndex( idx, 9 );
	console.log( '%d => [%d,%d] => %d', idx, 0, 9, out );
}
