'use strict';

var numel = require( '@stdlib/ndarray/base/numel' );
var ind2sub = require( './../lib' );

var shape = [ 3, 3, 3 ];
var len = numel( shape );

var arr = new Array( len );
var i;
for ( i = 0; i < len; i++ ) {
	arr[ i ] = i;
}

var opts = {
	'order': 'column-major'
};

console.log( '' );
console.log( 'Dimensions: %s.', shape.join( 'x' ) );
console.log( 'View (subscripts):' );
console.log( '' );

var row;
var s;

row = '  ';
for ( i = 0; i < len; i++ ) {
	s = ind2sub( shape, i, opts );
	row += '(' + s.join( ',' ) + ')';
	if ( (i+1)%shape[0] === 0 ) {
		console.log( row );
		row = '  ';
	} else {
		row += ', ';
	}
	if ( (i+1)%(shape[0]*shape[1]) === 0 ) {
		console.log( '' );
	}
}
