#include "stdlib/math/base/special/signbitf.h"
#include "stdlib/number/float32/base/to_word.h"

/**
* Returns a boolean indicating if the sign bit is on (true) or off (false).
*
* @param x       number
* @return        boolean indicating if sign bit is on or off
*/
bool stdlib_base_signbitf( const float x ) {
	// Get the number's unsigned 32-bit integer representation:
	uint32_t w = stdlib_base_float32_to_word( x );

	// Shift off all bits which are not the sign bit and check if the sign bit is on:
	return ( w >> 31 ) ? true : false;
}
