'use strict';

/**
* Returns vertical shift for aligning tick text.
*
* @private
* @param {string} orient - axis orientation
* @returns {string} text shift
*/
function dy( orient ) {
	if ( orient === 'top' ) {
		return '0em';
	}
	if ( orient === 'bottom' ) {
		return '.71em';
	}
	return '.32em';
}


// EXPORTS //

module.exports = dy;
