'use strict';

// MODULES //

var logger = require( 'debug' );
var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var isNull = require( '@stdlib/assert/is-null' );
var FLOAT64_MAX = require( '@stdlib/constants/math/float64-max' );


// VARIABLES //

var debug = logger( 'sparkline:set:buffer-size' );


// MAIN //

/**
* Sets the sliding window size.
*
* @param {(PositiveInteger|null)} size - sliding window size
* @throws {TypeError} must be a positive integer
* @throws {Error} must be greater than or equal to the number of data elements
*/
function set( size ) {
	/* eslint-disable no-invalid-this */
	if ( !isPositiveInteger( size ) && !isNull( size ) ) {
		throw new TypeError( 'invalid value. `bufferSize` must be a positive integer or null. Value: `' + size + '`.' );
	}
	if ( size === null ) {
		size = FLOAT64_MAX;
	}
	if ( size < this._data.length ) {
		throw new Error( 'invalid value. `bufferSize` size is smaller than the number of data elements. Number of elements: `'+ this._data.length + '`. Value: `' + size + '`.' );
	}
	debug( 'Current value: %s.', this._bufferSize );

	this._bufferSize = size;
	debug( 'New value: %s.', this._bufferSize );

	this.emit( 'change' );
}


// EXPORTS //

module.exports = set;
