'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var FLOAT64_MAX = require( '@stdlib/constants/math/float64-max' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'an instance throws an error if provided an invalid `bufferSize` value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		-5,
		0,
		3.14,
		NaN,
		void 0,
		{},
		[],
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var node = ctor();
			node.bufferSize = value;
		};
	}
});

tape( 'an instance throws an error if provided a `bufferSize` which is less than the number of data elements', function test( t ) {
	var values;
	var i;

	values = [
		1,
		2,
		3,
		4,
		5
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), RangeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var node = ctor({
				'data': [ 1, 2, 3, 4, 5, 6 ]
			});
			node.bufferSize = value;
		};
	}
});

tape( 'an instance supports setting and getting the property value', function test( t ) {
	var node;

	node = ctor({
		'bufferSize': 10
	});
	node._render = noop; // eslint-disable-line no-underscore-dangle
	t.strictEqual( node.bufferSize, 10, 'returns expected value' );

	node.bufferSize = 20;
	t.strictEqual( node.bufferSize, 20, 'returns expected value' );

	node.bufferSize = 30;
	t.strictEqual( node.bufferSize, 30, 'returns expected value' );

	node.bufferSize = null;
	t.strictEqual( node.bufferSize, FLOAT64_MAX, 'returns expected value' );

	t.end();
});

tape( 'setting the `bufferSize` property triggers a `change` event', function test( t ) {
	var node;

	node = ctor({
		'bufferSize': 10
	});
	node._render = noop; // eslint-disable-line no-underscore-dangle

	node.on( 'change', onChange );
	node.bufferSize = 30;

	function onChange() {
		t.ok( true, 'triggers event' );
		t.end();
	}
});
