'use strict';

// MODULES //

var tape = require( 'tape' );
var isArray = require( '@stdlib/assert/is-array' );
var Sparkline = require( './../lib' );
var getData = require( './../lib/get_data.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof getData, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns standardized chart data', function test( t ) {
	var expected;
	var context;
	var opts;
	var v;

	opts = {
		'data': [ 1, 2, 3 ]
	};
	context = new Sparkline( opts );

	v = getData.call( context );

	expected = [
		{
			'y': 1
		},
		{
			'y': 2
		},
		{
			'y': 3
		}
	];
	t.equal( isArray( v ), true, 'returns an array' );
	t.deepEqual( v, expected, 'returns expected data' );

	t.end();
});
