'use strict';

// MODULES //

var tape = require( 'tape' );
var yValue = require( './../lib/y_value' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof yValue, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns the input value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		true,
		null,
		undefined,
		{},
		[],
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( yValue( values[i] ), values[i], 'returns the input value' );
	}
	t.end();
});
