'use strict';

// MODULES //

var Sparkline = require( '@stdlib/plot/sparklines/unicode/base' );


// MAIN //

/**
* Unicode sparkline line chart constructor.
*
* @constructor
* @param {Options} [options] - chart options
* @param {(Array|TypedArray)} [options.data] - chart data
* @param {Function} [options.yValue] - y-value accessor
* @param {(FiniteNumber|null)} [options.yMin] - minimum value of the y-axis domain
* @param {(FiniteNumber|null)} [options.yMax] - maximum value of the y-axis domain
* @param {(PositiveInteger|null)} [options.window] - sliding window size
* @param {boolean} [options.infinities=false] - boolean indicating whether to encode infinite values
* @throws {TypeError} must provide valid options
* @returns {LineChart} chart instance
*
* @example
* var data = [ 1.0, 5.0, 3.0, 2.0, 4.0, 4.0, 3.0 ];
* var opts = {
*     'data': data
* };
* var chart = new LineChart( opts );
* var str = chart.render();
* // returns '⡈⠑⠢⠔⠒⠒⠒'
*/
function LineChart( options ) {
	if ( !(this instanceof LineChart) ) {
		if ( arguments.length ) {
			return new LineChart( options );
		}
		return new LineChart();
	}
	if ( arguments.length ) {
		Sparkline.call( this, options );
	} else {
		Sparkline.call( this );
	}
	return this;
}

/*
* Create a prototype which inherits from the parent prototype.
*/
LineChart.prototype = Object.create( Sparkline.prototype );

/*
* Set the constructor.
*/
LineChart.prototype.constructor = LineChart;

/**
* Renders a chart.
*
* @memberof LineChart.prototype
* @function render
* @returns {string} rendered chart
*
* @example
* var data = [ 1.0, 5.0, 3.0, 2.0, 4.0, 4.0, 3.0 ];
*
* var chart = new LineChart({
*     'data': data
* });
*
* var str = chart.render();
* // returns '⡈⠑⠢⠔⠒⠒⠒'
*/
LineChart.prototype.render = require( './render.js' );


// EXPORTS //

module.exports = LineChart;
