'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/random/base/randu' );
var pow = require( '@stdlib/math/base/special/pow' );
var Float64Array = require( '@stdlib/array/float64' );
var array = require( '@stdlib/ndarray/array' );
var pkg = require( './../package.json' ).name;
var UnicodeTable = require( './../lib' );


// FUNCTIONS //

/**
* Creates a benchmark function.
*
* @private
* @param {PositiveInteger} rows - number of rows
* @param {PositiveInteger} columns - number of columns
* @param {UnicodeTable} table - table instance
* @returns {Function} benchmark function
*/
function createBenchmark( rows, columns, table ) {
	var headers;
	var data;
	var i;

	data = new Float64Array( rows * columns );
	for ( i = 0; i < data.length; i++ ) {
		data[ i ] = randu();
	}
	data = array( data, {
		'shape': [ rows, columns ]
	});
	headers = [];
	for ( i = 0; i < columns; i++ ) {
		headers.push( randu() );
	}
	table.data = data;
	table.headers = headers;
	return benchmark;

	/**
	* Generates a row of table data.
	*
	* @private
	* @returns {Array<number>} table data
	*/
	function row() {
		var data;
		var i;

		data = [];
		for ( i = 0; i < columns; i++ ) {
			data.push( randu() );
		}
		return data;
	}

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var str;
		var i;

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			str = table.push( row() ).render();
			if ( typeof str !== 'string' ) {
				b.fail( 'should return a string' );
			}
		}
		b.toc();
		if ( typeof str !== 'string' ) {
			b.fail( 'should return a string' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
}


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var columns;
	var table;
	var rows;
	var min;
	var max;
	var f;
	var i;
	var j;

	min = 1; // 5^min
	max = 3; // 5^max

	for ( i = min; i <= max; i++ ) {
		for ( j = min; j <= max; j++ ) {
			rows = pow( 5, i );
			columns = pow( 5, j );

			table = new UnicodeTable();
			table.bufferSize = rows;
			f = createBenchmark( rows, columns, table );
			bench( pkg+':render:rows='+rows+',columns='+columns, f );
		}
	}
}

main();
