'use strict';

var uniform = require( '@stdlib/random/array/uniform' );
var array = require( '@stdlib/ndarray/array' );
var ctor = require( './../lib' );

var headers;
var table;
var data;
var str;

// Generate some random data...
data = uniform( 50, 1, 100 );
data = array( data, {
	'shape': [ 10, 5 ]
});

// Generate headers...
headers = [ 'A', 'B', 'C', 'D', 'E' ];

// Create a table:
table = ctor( data, headers );

// Render the table:
str = table.render();
console.log( str );
// => '...'
