'use strict';

// MODULES //

var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );


// MAIN //

/**
* Returns default options.
*
* @private
* @returns {Object} default options
*/
function defaults() {
	var out = {};

	// Cell alignment:
	out.alignment = 'right';

	// Boolean indicating whether to re-render on a `change` event:
	out.autoRender = false;

	// Border characters:
	out.borders = [ '─', '│', '─', '│' ];

	// Buffer size:
	out.bufferSize = FLOAT64_MAX;

	// Cell padding:
	out.cellPaddingLeft = 1;
	out.cellPaddingRight = 1;

	// Corner characters:
	out.corners = [ '┌', '┐', '┘', '└' ];

	// Column separator character:
	out.columnSeparator = '│';

	// Header separator character:
	out.headerSeparator = '─';

	// Joint characters:
	out.joints = [ '┼', '┬', '┤', '┴', '├' ];

	// Line separator mode:
	out.horizontalSeparatorMode = 'resume';
	out.verticalSeparatorMode = 'resume';

	// Output margins:
	out.marginX = 0;
	out.marginY = 0;

	// Maximum cell width:
	out.maxCellWidth = FLOAT64_MAX;

	// Maximum output width:
	out.maxOutputWidth = FLOAT64_MAX;

	// Row separator character:
	out.rowSeparator = '';

	return out;
}


// EXPORTS //

module.exports = defaults;
