'use strict';

// MAIN //

/**
* Parses an array into table headers.
*
* @private
* @param {Collection} headers - column names
* @param {Array<NonNegativeInteger>} [columnWidths] - column widths
* @returns {Object} table metadata
*/
function parseHeaders( headers, columnWidths ) {
	var header;
	var i;

	if ( columnWidths ) {
		for ( i = 0; i < headers.length; i++ ) {
			header = String( headers[ i ] );
			if ( header.length > columnWidths[ i ] ) {
				columnWidths[ i ] = header.length;
			}
		}
	} else {
		columnWidths = [];
		for ( i = 0; i < headers.length; i++ ) {
			columnWidths.push( String( headers[ i ] ).length );
		}
	}
	return {
		'headers': headers,
		'columnWidths': columnWidths,
		'data': null
	};
}


// EXPORTS //

module.exports = parseHeaders;
