'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' );
var isNonNegativeIntegerArray = require( '@stdlib/assert/is-nonnegative-integer-array' );
var isEqual = require( '@stdlib/assert/deep-equal' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the cell's left padding(s).
*
* @private
* @param {(Array<NonNegativeInteger>|NonNegativeInteger)} padding - cell's left padding(s)
* @throws {TypeError} must provide a non-negative integer
* @throws {RangeError} must provide correct number of columns if providing an array
*/
function set( padding ) {
	/* eslint-disable no-invalid-this */
	if ( !isNonNegativeInteger( padding ) && !isNonNegativeIntegerArray( padding ) ) { // eslint-disable-line max-len
		throw new TypeError( format( 'invalid assignment. `%s` must be a non-negative integer or an array of non-negative integers. Value: `%s`.', 'cellPadding', padding ) );
	}
	if ( isNonNegativeIntegerArray( padding ) ) {
		if ( this._numColumns ) {
			if ( padding.length !== this._numColumns ) {
				throw new RangeError( format( 'invalid assignment. `%s` must have `%d` number of columns. Value: `%d`.', 'padding', this._numColumns, padding.length ) );
			}
		} else {
			this._numColumns = padding.length;
		}
	}
	if ( !isEqual( padding, this._cellPaddingLeft ) ) {
		this._cellPaddingLeft = padding;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
