'use strict';

// MODULES //

var isArray = require( '@stdlib/assert/is-array' );


// MAIN //

/**
* Returns the cell's right padding(s).
*
* @private
* @returns {(Array<NonNegativeInteger>|NonNegativeInteger)} cell's right padding(s)
*/
function get() {
	/* eslint-disable no-invalid-this */
	if ( isArray( this._cellPaddingRight ) ) {
		return this._cellPaddingRight.slice();
	}
	return this._cellPaddingRight;
}


// EXPORTS //

module.exports = get;
