'use strict';

// MODULES //

var format = require( '@stdlib/string/format' );
var isMatrixLike = require( '@stdlib/assert/is-matrix-like' );
var isArrayArray = require( '@stdlib/assert/is-array-array' );
var ndarray2array = require( '@stdlib/ndarray/to-array' );
var isEqual = require( '@stdlib/assert/deep-equal' );
var parse2dArray = require( './../../parser/parse_2d_array.js' );


// MAIN //

/**
* Sets the table data.
*
* @private
* @param {Array<Array>|MatrixLike} data - table data
* @throws {TypeError} must be a 2 dimensional array/ndarray
* @throws {RangeError} must provide correct number of columns
* @throws {RangeError} rows must not exceed maximum data buffer size
*/
function set( data ) {
	/* eslint-disable no-invalid-this */
	var parsed;
	if ( isMatrixLike( data ) ) {
		data = ndarray2array( data );
	}
	if ( !isArrayArray( data ) ) {
		throw new TypeError( format( 'invalid argument. `%s` must be an object, array of objects, or a 2 dimensional array/ndarray. Value: `%s`.', 'data', data ) );
	}
	parsed = parse2dArray( data, this._headers );
	if ( parsed.data.length > this._bufferSize ) {
		throw new RangeError( format( 'invalid assignment. `%s` length exceeds maximum data buffer size. Buffer size: `%u`. Length: `%u`.', 'data', this._bufferSize, parsed.data.length ) );
	}
	if ( this._numColumns ) {
		if ( parsed.columnWidths.length !== this._numColumns ) {
			throw new RangeError( format( 'invalid assignment. `%s` must have `%d` number of columns. Value: `%d`.', 'data', this._numColumns, parsed.columnWidths.length ) );
		}
	} else {
		this._numColumns = parsed.columnWidths.length;
	}
	if ( !isEqual( parsed.data, this._data ) ) {
		this._data = parsed.data;
		this._columnWidths = parsed.columnWidths;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
