'use strict';

// MODULES //

var isStringArray = require( '@stdlib/assert/is-string-array' );
var numGraphemeClusters = require( '@stdlib/string/num-grapheme-clusters' );
var isEqual = require( '@stdlib/assert/deep-equal' );
var format = require( '@stdlib/string/format' );


// VARIABLES //

var NUM_JOINTS = 5;


// MAIN //

/**
* Sets the joint characters.
*
* @private
* @param {Array<string>} joints - joint characters
* @throws {TypeError} must be an array of five grapheme clusters
*/
function set( joints ) {
	/* eslint-disable no-invalid-this */
	var i;

	if ( !isStringArray( joints ) || joints.length !== NUM_JOINTS ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be an array of five grapheme clusters. Value: `%s`.', 'joints', joints ) );
	}
	for ( i = 0; i < NUM_JOINTS; i++ ) {
		if ( numGraphemeClusters( joints[ i ] ) !== 1 ) {
			throw new TypeError( format( 'invalid assignment. `%s` must be an array of five grapheme clusters. Value: `%s`.', 'joints', joints ) );
		}
	}
	if ( !isEqual( joints, this._joints ) ) {
		this._joints = joints;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
