'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the table's vertical margin.
*
* @private
* @param {NonNegativeInteger} y - Y margin
* @throws {TypeError} must provide a non-negative integer
*/
function set( y ) {
	/* eslint-disable no-invalid-this */
	if ( !isNonNegativeInteger( y ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a non-negative integer. Value: `%s`.', 'marginY', y ) );
	}
	if ( y !== this._marginY ) {
		this._marginY = y;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
