'use strict';

// MODULES //

var isNonNegativeInteger = require( '@stdlib/assert/is-nonnegative-integer' );
var isNull = require( '@stdlib/assert/is-null' );
var format = require( '@stdlib/string/format' );
var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );


// MAIN //

/**
* Sets the table's max output width.
*
* @private
* @param {(NonNegativeInteger|null)} width - max output width
* @throws {TypeError} must provide a non-negative integer or null
*/
function set( width ) {
	/* eslint-disable no-invalid-this */
	var FLG = isNull( width );
	if ( !isNonNegativeInteger( width ) && !FLG ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a non-negative integer or null. Value: `%s`.', 'maxOutputWidth', width ) );
	}
	if ( FLG ) {
		width = FLOAT64_MAX;
	}
	if ( width !== this._maxOutputWidth ) {
		this._maxOutputWidth = width;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
