'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' );
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Sets the row separator character(s).
*
* @private
* @param {string} separator - row separator
* @throws {TypeError} must be a string
*/
function set( separator ) {
	/* eslint-disable no-invalid-this */
	if ( !isString( separator ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a string. Value: `%s`.', 'rowSeparator', separator ) );
	}
	if ( separator !== this._rowSeparator ) {
		this._rowSeparator = separator;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
