'use strict';

// MODULES //

var contains = require( '@stdlib/array/base/assert/contains' ).factory;
var isString = require( '@stdlib/assert/is-string' );
var format = require( '@stdlib/string/format' );


// VARIABLES //

var isValidMode = contains( [ 'resume', 'interpolate', 'repeat' ] );


// MAIN //

/**
* Sets the table's vertical line separator mode.
*
* @private
* @param {string} mode - line separator mode
* @throws {TypeError} must be a supported line separator mode string
*/
function set( mode ) {
	/* eslint-disable no-invalid-this */
	if ( !isString( mode ) || !isValidMode( mode ) ) {
		throw new TypeError( format( 'invalid assignment. `%s` must be a valid line separator mode string. Value: `%s`.', 'lineSeparatorMode', mode ) );
	}
	if ( mode !== this._verticalSeparatorMode ) {
		this._verticalSeparatorMode = mode;
		this.emit( 'change' );
	}
}


// EXPORTS //

module.exports = set;
