'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'an instance throws an error if provided an invalid `alignment` value', function test( t ) {
	var values;
	var i;

	values = [
		5,
		NaN,
		true,
		false,
		null,
		void 0,
		{},
		[],
		'1234',
		'middle',
		[ 4, 'hello', null ],
		[ 'beep', 'boop' ]
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var table = ctor();
			table.alignment = value;
		};
	}
});

tape( 'an instance supports setting and getting the property value', function test( t ) {
	var table;
	var arr;

	table = ctor( null, {
		'alignment': 'right'
	});
	table.render = noop;

	t.strictEqual( table.alignment, 'right', 'returns expected value' );

	table.alignment = 'left';
	t.strictEqual( table.alignment, 'left', 'returns expected value' );

	table.alignment = 'center';
	t.strictEqual( table.alignment, 'center', 'returns expected value' );

	arr = [ 'left', 'right', 'center' ];
	table.alignment = arr;
	t.notEqual( table.alignment, arr, 'returns a new reference' );
	t.deepEqual( table.alignment, arr, 'returns expected value' );

	t.end();
});

tape( 'an instance throws an error if provided an `alignment` array with incorrect number of columns', function test( t ) {
	t.throws( badValue, RangeError, 'throws an error' );
	t.end();

	function badValue() {
		var table = ctor( [ [ 1, 2, 3 ], [ 4, 5, 6 ] ] ); // 3 columns
		table.alignment = [ 'left', 'right' ]; // 2 columns
	}
});

tape( 'settings the `alignment` property to an array sets number of columns', function test( t ) {
	t.throws( badValue, RangeError, 'throws an error' );
	t.end();

	function badValue() {
		var table = ctor( null, {
			'alignment': [ 'left', 'center', 'right' ] // 3 columns
		});
		table.data = [ [ 1, 2 ], [ 4, 5 ] ]; // 2 columns
	}
});

tape( 'setting the `alignment` property to a new value triggers a `change` event', function test( t ) {
	var table;

	table = ctor( null, {
		'alignment': 'right'
	});
	table.render = noop;

	table.on( 'change', onChange );
	table.alignment = [ 'left', 'right' ];

	function onChange() {
		t.ok( true, 'triggers event' );
		t.end();
	}
});

tape( 'setting the `alignment` property to its current value does not trigger a `change` event', function test( t ) {
	var table;

	table = ctor( null, {
		'alignment': [ 'left', 'right' ]
	});
	table.render = noop;

	table.on( 'change', onChange );
	table.alignment = [ 'left', 'right' ];

	t.ok( true, 'does not trigger event' );
	t.end();

	function onChange() {
		t.ok( false, 'should not trigger event' );
	}
});
