'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'an instance throws an error if provided an invalid `headers` value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		null,
		void 0,
		true,
		false,
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var table = ctor();
			table.data = value;
		};
	}
});

tape( 'an instance supports setting and getting the property value', function test( t ) {
	var table;
	var arr;

	arr = [ 'col1', 'col2' ];
	table = ctor( null, arr );
	table.render = noop;
	t.notEqual( table.headers, arr, 'returns a new reference' );
	t.deepEqual( table.headers, arr, 'returns expected value' );

	arr = [ 1, 2 ];
	table.headers = arr;
	t.notEqual( table.headers, arr, 'returns a new reference' );
	t.deepEqual( table.headers, arr, 'returns expected value' );

	arr = [ null, undefined ];
	table.headers = arr;
	t.notEqual( table.headers, arr, 'returns a new reference' );
	t.deepEqual( table.headers, arr, 'returns expected value' );

	t.end();
});

tape( 'an instance throws an error if provided `headers` with incorrect number of columns', function test( t ) {
	t.throws( badValue, RangeError, 'throws an error' );
	t.end();

	function badValue() {
		var table = ctor( [ [ 1, 2, 3 ], [ 4, 5, 6 ] ] ); // 3 columns
		table.headers = [ 'col1', 'col2' ]; // 2 columns
	}
});

tape( 'settings the `headers` property sets number of columns', function test( t ) {
	t.throws( badValue, RangeError, 'throws an error' );
	t.end();

	function badValue() {
		var table = ctor( null, [ 'col1', 'col2', 'col3' ] ); // 3 columns
		table.data = [ [ 1, 2 ], [ 3, 4 ] ]; // 2 columns
	}
});

tape( 'setting the `headers` property to a new value triggers a `change` event', function test( t ) {
	var table;

	table = ctor( null, [ 'col1', 'col2' ] );
	table.render = noop;

	table.on( 'change', onChange );
	table.headers = [ 1, 2 ];

	function onChange() {
		t.ok( true, 'triggers event' );
		t.end();
	}
});

tape( 'setting the `headers` property to its current value does not trigger a `change` event', function test( t ) {
	var table;

	table = ctor( null, [ 'col1', 'col2' ] );
	table.render = noop;

	table.on( 'change', onChange );
	table.headers = [ 'col1', 'col2' ];

	t.ok( true, 'does not trigger event' );
	t.end();

	function onChange() {
		t.ok( false, 'should not trigger event' );
	}
});
