'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var FLOAT64_MAX = require( '@stdlib/constants/float64/max' );
var ctor = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof ctor, 'function', 'main export is a function' );
	t.end();
});

tape( 'an instance throws an error if provided an invalid `maxCellWidth` value', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		2.4,
		-3,
		true,
		void 0,
		{},
		function noop() {},
		[ 4, 'hello', null ],
		[ 2, -5, 3.4 ]
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			var table = ctor();
			table.maxCellWidth = value;
		};
	}
});

tape( 'an instance supports setting and getting the property value', function test( t ) {
	var table;
	var arr;

	table = ctor( null, {
		'maxCellWidth': 2
	});
	t.strictEqual( table.maxCellWidth, 2, 'returns expected value' );

	table.maxCellWidth = 3;
	t.strictEqual( table.maxCellWidth, 3, 'returns expected value' );

	table.maxCellWidth = null;
	t.strictEqual( table.maxCellWidth, FLOAT64_MAX, 'returns expected value' );

	arr = [ 0, 5 ];
	table.maxCellWidth = arr;
	t.notEqual( table.maxCellWidth, arr, 'returns a new reference' );
	t.deepEqual( table.maxCellWidth, arr, 'returns expected value' );

	arr = [ null, 5 ];
	table.maxCellWidth = arr;
	t.notEqual( table.maxCellWidth, arr, 'returns a new reference' );
	t.deepEqual( table.maxCellWidth, [ FLOAT64_MAX, 5 ], 'returns expected value' );

	t.end();
});

tape( 'an instance throws an error if provided a `maxCellWidth` array with incorrect number of columns', function test( t ) {
	t.throws( badValue, RangeError, 'throws an error' );
	t.end();

	function badValue() {
		var table = ctor( [ [ 1, 2, 3 ], [ 4, 5, 6 ] ] ); // 3 columns
		table.maxCellWidth = [ 1, 2 ]; // 2 columns
	}
});

tape( 'settings the `maxCellWidth` property to an array sets number of columns', function test( t ) {
	t.throws( badValue, RangeError, 'throws an error' );
	t.end();

	function badValue() {
		var table = ctor( null, {
			'maxCellWidth': [ 1, 2, 3 ] // 3 columns
		});
		table.data = [ [ 1, 2 ], [ 4, 5 ] ]; // 2 columns
	}
});

tape( 'setting the `maxCellWidth` property to a new value triggers a `change` event', function test( t ) {
	var table;

	table = ctor( null, {
		'maxCellWidth': 2
	});
	table.render = noop;

	table.on( 'change', onChange );
	table.maxCellWidth = [ 1, 2 ];

	function onChange() {
		t.ok( true, 'triggers event' );
		t.end();
	}
});

tape( 'setting the `maxCellWidth` property to its current value does not trigger a `change` event', function test( t ) {
	var table;

	table = ctor( null, {
		'maxCellWidth': [ 2, 3 ]
	});
	table.render = noop;

	table.on( 'change', onChange );
	table.maxCellWidth = [ 2, 3 ];

	t.ok( true, 'does not trigger event' );
	t.end();

	function onChange() {
		t.ok( false, 'should not trigger event' );
	}
});
