'use strict';

// VARIABLES //

// See [Block Elements]{@link https://en.wikipedia.org/wiki/Block_Elements}.
var UPPER_BLOCK = '▀'; // U+2580
var LOWER_BLOCK = '▄'; // U+2584

var MIDDLE = '─'; // U+2500
var MISSING_VALUE = ' ';


// RENDER //

/**
* Renders a chart.
*
* @private
* @returns {string} rendered chart
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var str;
	var len;
	var d;
	var i;

	len = this._data.length;
	if ( len === 0 ) {
		return '';
	}
	// Generate the sparkline chart, assigning a glyph to each datum...
	str = '';

	// TODO: color encoding: one color for both pos and neg or two colors for separate colors
	// TODO: "loss" values diff color (red)

	for ( i = 0; i < len; i++ ) {
		d = this._data[ i ];
		if ( d.flg === 'nan' ) {
			str += MISSING_VALUE;
			continue;
		}
		if ( d.y < 0.0 ) {
			str += LOWER_BLOCK;
		} else if ( d.y === 0.0 ) {
			str += MIDDLE;
		} else {
			str += UPPER_BLOCK;
		}
	}
	return str;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
