tape( 'when called without arguments, the function returns a function that returns `NaN` if minimum support `a` is not less than maximum support `b`', function test( t ) {
	var arcsine;
	var values;
	var r;
	var i;

	arcsine = factory();
	values = [
		[ 2.0, 1.0 ],
		[ -3.0, -4.0 ],
		[ 0.0, 0.0 ]
	];

	for ( i = 0; i < values.length; i++ ) {
		r = arcsine( values[ i ][ 0 ], values[ i ][ 1 ] );
		t.strictEqual( isnan( r ), true, 'returns `NaN` when provided '+values[ i ] );
	}
	t.end();
});

tape( 'the function returns a PRNG for generating random numbers from an arcsine distribution', opts, function test( t ) {
	var threshold;
	var count;
	var npass;
	var N;
	var a;
	var b;
	var x;

	threshold = 0.10;

	a = 2.0;
	b = 4.0;

	x = new Array( 1e3 );
	N = 500;

	count = -1;
	npass = 0;

	gof();

	function gof() {
		var rejected;
		var arcsine;
		var pValue;
		var bool;
		var i;
		var j;

		count += 1;
		rejected = 0;
		for ( i = 0; i < N; i++ ) {
			arcsine = factory( a, b );
			t.ok( true, 'seed: '+arcsine.SEED );
			for ( j = 0; j < x.length; j++ ) {
				x[ j ] = arcsine();
				if ( x[ j ] < a || x[ j ] > b ) {
					t.ok( false, 'returned a number outside support: '+x[ j ] );
				}
			}
			// Test using Kolmogorov-Smirnov goodness-of-fit test:
			pValue = kstest( x, 'arcsine', a, b ).pValue;
			t.equal( typeof pValue, 'number', 'returns a p-value: '+pValue );
			if ( pValue < 0.05 ) {
				rejected += 1;
			}
		}
		// Account for small sample size and few repeats...
		bool = ( rejected / N < threshold );

		// If we succeed the first time, we are done...
		if ( count === 0 && bool ) {
			return done( bool, rejected );
		}
		// Retry mode...
		if ( bool ) {
			npass += 1;
		}
		// Retry twice...
		if ( count < 2 ) {
			return gof();
		}
		// Both retries must succeed for test to pass:
		bool = ( npass >= 2 );
		return done( bool, rejected );
	}

	function done( bool, rejected ) {
		t.ok( bool, 'null hypothesis (i.e., that numbers are drawn from Arcsine('+a+','+b+')) is rejected in less than '+(threshold*100)+'% of cases ('+rejected+' of '+N+'). Repeats: '+npass+' of '+count+'.' );
		t.end();
	}
});
