'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/random/base/randu' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var EPS = require( '@stdlib/constants/math/float64-eps' );
var pkg = require( './../package.json' ).name;
var frechet = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( assert ) {
	var alpha;
	var s;
	var m;
	var z;
	var i;

	assert.tic();
	for ( i = 0; i < assert.iterations; i++ ) {
		alpha = ( randu()*100.0 ) + EPS;
		s = ( randu()*100.0 ) + EPS;
		m = ( randu()*100.0 ) - 50.0;
		z = frechet( alpha, s, m );
		if ( isnan( z ) ) {
			assert.fail( 'should not return NaN' );
		}
	}
	assert.toc();
	if ( isnan( z ) ) {
		assert.fail( 'should not return NaN' );
	}
	assert.pass( 'benchmark finished' );
	assert.end();
});

bench( pkg+':factory', function benchmark( assert ) {
	var alpha;
	var rand;
	var s;
	var m;
	var z;
	var i;

	alpha = 200.0;
	s = 440.0;
	m = 383.5;
	rand = frechet.factory( alpha, s, m );

	assert.tic();
	for ( i = 0; i < assert.iterations; i++ ) {
		z = rand();
		if ( isnan( z ) ) {
			assert.fail( 'should not return NaN' );
		}
	}
	assert.toc();
	if ( isnan( z ) ) {
		assert.fail( 'should not return NaN' );
	}
	assert.pass( 'benchmark finished' );
	assert.end();
});
