'use strict';

/**
* Geometric distributed pseudorandom numbers.
*
* @module @stdlib/random/base/geometric
*
* @example
* var geometric = require( '@stdlib/random/base/geometric' );
*
* var v = geometric( 0.5 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/random/base/geometric' ).factory;
*
* var geometric = factory( 0.3, {
*     'seed': 297
* });
*
* var v = geometric();
* // returns <number>
*
* @example
* var factory = require( '@stdlib/random/base/geometric' ).factory;
*
* var geometric = factory({
*     'seed': 297
* });
*
* var v = geometric( 0.5 );
* // returns <number>
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var geometric = require( './geometric.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( geometric, 'factory', factory );


// EXPORTS //

module.exports = geometric;
